import React, { Component } from "react";
import Productos from "../Components/Productos.jsx";
import firebase from "../Config/firebase";
import { CardGroup, Col, Container, Spinner, Row } from 'react-bootstrap';
import imgAddItem from "../../src/imgAdd64.svg";
import { Link } from 'react-router-dom'


class Home extends Component {
    constructor() {
        super();
        this.state = {
            productos: [],
            loading: true
        }
    }
    componentDidMount() {
        firebase.db.collection("productos")
            .get()
            .then(querySnapshot => {
                this.setState({
                    productos: querySnapshot.docs,
                    loading: false
                })
            })
    }
    render() {
        if (this.state.loading) {
            return (
                <Container>
                    <div style={{ position: "fixed", top: "50%", left: "50%" }}>
                        <Spinner animation="grow" />
                        <Spinner animation="grow" />
                        <Spinner animation="grow" />
                    </div>
                </Container>
            )
        } else {
            return (
                <Container>
                    <div id ="subtituloHome">
                        <Row>
                            <Col><h2 className="mt-3 text-dark" id="subtituloProductos" >Productos</h2></Col>
                            <Col className="mt-1" ><Link to={"/producto/alta"}><img src={imgAddItem} style={{ width: '60px' }}></img></Link></Col>
                        </Row>
                    </div>
                    <CardGroup >
                        {this.state.productos.map((producto, i) => <Productos key={producto.id} productos={producto} />)}
                    </CardGroup>
                </Container>
            )
        }

    }
}

export default Home;
