import React, { useState, useEffect } from "react";
import "../Styles/App.css";

function ProductoDetalle(props) {
    const [producto, setProducto] = useState({})

    useEffect(
        () => {
            fetch("https://jsonfy.com/items/" + props.match.params.id)
                .then(res => res.json())
                .then((data) => {
                    console.log(data);
                    setProducto(data[0])
                })
        }, []
    )

    return (
        <div>
            <h3>Detalle de producto</h3>
            <div>
                <table >
                    <tr>
                        <td width="20%"><h4 >Nombre:</h4></td>
                        <td> {producto.name}</td>
                    </tr>

                    <tr>
                        <td><h4 >Descripci√≥n:</h4></td>
                        <td> <div>{producto.description}</div></td>
                    </tr>

                    <tr>
                        <td><h4>Precio:</h4></td>
                        <td> <div>$ {producto.price}</div></td>
                    </tr>

                    <tr>
                        <td><h4>Cantidad disponible:</h4></td>
                        <td> <div>{producto.stock}</div></td>
                    </tr>
                </table>
                <img width="30%" src={producto.photo_url}></img>
            </div>
        </div>
    )
}

export default ProductoDetalle;
