import React, { Component } from "react";
import './Producto.css';

class Producto extends Component {
  constructor() {
    super();

    this.state = {
      mensaje: ""
    }
  }

  comprar = () => {
    this.setState({ mensaje: "Gracias por su compra" });
  }

  render() {
    const productoDetalle = this.props.productoDetalle;

    return (

      <div>
        <h1 className="titulo_1"> Finalizar compra </h1>
        <h2 className="titulo_2">Detalle de producto</h2>

        <table>
          <tr>
            <td><h4>Nombre:</h4></td>
            <td> {productoDetalle.productoNombre}</td>
          </tr>

          <tr>
            <td><h4>Descripción:</h4></td>
            <td> <div>{productoDetalle.productoNombre}</div></td>
          </tr>

          <tr>
            <td><h4>Precio:</h4></td>
            <td> <div>{productoDetalle.productoNombre}</div></td>
          </tr>

          <tr>
            <td><h4>SKU:</h4></td>
            <td> <div>{productoDetalle.productoNombre}</div></td>
          </tr>

          <tr>
            <td><h4>Cantidad:</h4></td>
            <td> <div>{productoDetalle.productoNombre}</div></td>
          </tr>

          <div>Descripción: {productoDetalle.productoDescripcion}</div>
          <div>: {productoDetalle.productoPrecio}</div>
          <div>: {productoDetalle.productoSKU}</div>
          <div>: {productoDetalle.productoCantidad}</div>
        </table>

        <button className="boton" onClick={this.comprar}>Comprar</button>
        <div>{this.state.mensaje}</div>



      </div>

    )
  }
}

export default Producto;
