import React, { Component } from "react";
import ProductoDetalle from "./ProductoDetalle";
import Productos from "./Productos.jsx"

class Home extends Component {
    constructor() {
        super();
        this.state = {
            productos: [],
            loading: true
        }
    }
    componentDidMount() {
        fetch("https://jsonfy.com/items")
            .then(res => res.json())
            .then((data) => {
                this.setState({
                    productos: data,
                    loading: false
                })
            },
                (error) => {
                    console.log(error)
                })
            .catch(error => {
                console.log(error)
            })

    }
    render() {
        if (this.state.loading) {
            return (
                <div>
                    Cargando ...
                </div>
            )
        } else {
            return (
                <div>
                    <h2>Productos</h2>
                    {this.state.productos.map(producto => <Productos productos={producto} />)}
                </div>
            )
        }

    }
}

export default Home;
