import { Link } from 'react-router-dom'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

function Productos(props) {
  return (
    <div>
      <Card className="mr-3 mt-3 mb-3 text-center" style={{ width: '20rem', height: '25rem'}}>
        <Card.Body>
          <Card.Title>{props.productos.data().name}</Card.Title>
          <Card.Img src={props.productos.data().img} id="imgProductos "style={{ width: '15rem' }}></Card.Img>
          <Card.Text className="text-dark" id="priceProducto"> ${props.productos.data().price}</Card.Text>       
        </Card.Body>

        <Link to={"/productos/" + props.productos.id}><Button id="btnProductos" className="mr-1" variant="primary">Ver</Button></Link>
        <Link to={"/productos/editar/" + props.productos.id}><Button id="btnProductos" variant="primary">Editar</Button></Link>
      </Card>

    </div>
  );
}

export default Productos;
