import React from "react";
import MenuOpciones from "./MenuOpciones"
import { Navbar, Nav, FormLabel } from 'react-bootstrap'
import NetContext from "../../Context/NetContext"
function Menu(props) {

    return (
        <NetContext.Consumer>
            {
                context =>
                    <div>
                        <h1 className="mb-1 text-dark">WEB-SHOP</h1>
                        <Navbar expand="lg" className="navbar" bg="dark" variant="blue">
                            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                            <Navbar.Collapse id="navbarNavAltMarkup">
                                {
                                    context.login && <MenuOpciones  opcion={{Label: "Home", path: "/"}}/>
                                }
                                {
                                    !context.login &&
                                    <>
                                        <MenuOpciones  opcion={{Label: "Registro", path: "/registro"}}/>
                                        <MenuOpciones  opcion={{Label: "Ingresar", path: "/login"}}/>
                                    </>
                                }





                                <Nav className="mr-auto">
                                    {props.data.map(opcion => <MenuOpciones key={opcion.label} opcion={opcion} />)}
                                </Nav>
                            </Navbar.Collapse>
                        </Navbar>
                    </div>
            }

        </NetContext.Consumer>
    )
}

export default Menu;
