import React, { useState, useEffect } from 'react';
import firebase from '../Config/firebase';
import FormGroup from 'react-bootstrap/FormGroup';
import { Link } from 'react-router-dom'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

function ProductoEditar(props) {
    const [datos, setDatos] = useState({ name: '', price: '' });

    useEffect(
        () => {
            const id = props.match.params.id;
            firebase.db.doc("productos/" + id)
                .get()
                .then(doc => {
                    setDatos(doc.data())
                    console.log(doc.data())
                })
        }, []);
    const handleChange = (e) => {
        const target = e.target;
        const value = target.value
        const name = target.name

        setDatos({
            ...datos,
            [name]: value
        })
    }

    const handleSubmit = (e) => {
        console.log(datos)
        const id = props.match.params.id;
        firebase.db.doc("productos/" + id)
            .set({
                name: datos.name,
                price: datos.price
            }, { merge: true })
            .then(doc => {
                console.log(doc)
            })
        e.preventDefault();
    }

    const handleDelete = (e) => {
        const id = props.match.params.id;
        console.log("Eliminar", id)
        firebase.db.doc("productos/" + id)
            .delete()
            .then(doc => {
                console.log(doc)
            })
    }
    return (
        <Card style={{ width: 'expand', margin: 'auto' }}>
            <Card.Body>
                <Form onSubmit={handleSubmit}>
                    <FormGroup label="Nombre" type="text" name="name" value={datos.name} onChange={handleChange} />
                    <FormGroup type="number" name="price" value={datos.price} onChange={handleChange} />
                </Form>
                <Button onClick={handleChange}>Guardar</Button>
                <Button onClick={handleDelete}>Eliminar</Button>
                <Link to={"/"}><Button variant="primary">Cancelar</Button></Link>
            </Card.Body>
        </Card>
    )
}

export default ProductoEditar
