import React, { Component } from "react";
import Productos from "../Components/Productos.jsx";
import firebase from "../Config/firebase";
import { CardGroup, Container, Spinner, Card } from 'react-bootstrap';

class Home extends Component {
    constructor() {
        super();
        this.state = {
            productos: [],
            loading: true
        }
    }
    componentDidMount() {
        firebase.db.collection("productos")
            .get()
            .then(querySnapshot => {
                this.setState({
                    productos: querySnapshot.docs,
                    loading: false
                })
            })
    }
    render() {
        if (this.state.loading) {
            return (
                <Container>
                    <div style={{ position: "fixed", top: "50%", left: "50%" }}>
                        <Spinner animation="grow" />
                        <Spinner animation="grow" />
                        <Spinner animation="grow" />
                    </div>
                </Container>
            )
        } else {
            return (
                <Container>
                    <h2 className="mt-3 text-primary" >Productos</h2>
                    <CardGroup >
                        {this.state.productos.map((producto, i) => <Producto key={producto.id} productos={producto} />)}
                    </CardGroup>
                </Container>
            )
        }

    }
}

export default Home;
