import React, { useState, useEffect } from "react";
import firebase from "../Config/firebase"
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import { Link } from 'react-router-dom'

function ProductoDetalle(props) {

    const [producto, setProducto] = useState({})
    useEffect(
        () => {
            firebase.db.doc("productos/" + props.match.params.id)
                .get()
                .then(doc => {
                    console.log("doc", doc.data())
                    setProducto(doc.data())
                })
        }, []
    )

    return (
        <Card>
            <Card.Body>
                <Card.Title>{producto.name}</Card.Title>
                <Card.Img src={producto.img} style={"with:20px"}></Card.Img>
                <Card.Text>Descripci√≥n: {producto.description}</Card.Text>
                <Card.Text>SKU: {producto.SKU}</Card.Text>
                <Card.Text>${producto.price}</Card.Text>
                <Button className="bg-success" >Comprar</Button>
                <Link to={"/"}><Button className="ml-2 bg-primary">Volver</Button></Link>
            </Card.Body>
        </Card>

    )

}

export default ProductoDetalle;
