import React, { useState, useEffect } from "react";

function ProductoDetalle(props) {
    const [producto, setProducto] = useState({})

    useEffect(
        () => {
            fetch("https://jsonfy.com/items/" + props.match.params.id)
                .then(res => res.json())
                .then((data) => {
                    console.log(data);
                    setProducto(data[0])
                })
        }, []
    )

    return (
        <div>
            <h2 className="titulo_2">Detalle de producto</h2>
            <div>
                <table>
                    <tr>
                        <td><h4 className="titulo_detalle">Nombre:</h4></td>
                        <td> {producto.name}</td>
                    </tr>

                    <tr>
                        <td><h4 className="titulo_detalle">Descripci√≥n:</h4></td>
                        <td> <div>{producto.description}</div></td>
                    </tr>

                    <tr>
                        <td><h4 className="titulo_detalle">Precio:</h4></td>
                        <td> <div>{producto.price}</div></td>
                    </tr>
                </table>
            </div>
        </div>
    )
}

export default ProductoDetalle;
