import React, { useState, useEffect } from 'react';
import firebase from '../Config/firebase';
import { Link } from 'react-router-dom'
import Button from 'react-bootstrap/Button'
import {Card, Form} from 'react-bootstrap'
import FormGroup from "../Components/Forms/FormGroup"

function ProductoEditar(props) {
    const [datos, setDatos] = useState({ name: '', price: '' });

    useEffect(
        () => {
            const id = props.match.params.id;
            firebase.db.doc("productos/" + id)
                .get()
                .then(doc => {
                    setDatos(doc.data())
                    console.log(doc.data())
                })
        }, []);
    const handleChange = (e) => {
        const target = e.target;
        const value = target.value
        const name = target.name

        setDatos({
            ...datos,
            [name]: value
        })
    }

    const handleSubmit = (e) => {
        console.log(datos)
        const id = props.match.params.id;
        firebase.db.doc("productos/" + id)
            .set({
                name: datos.name,
                price: datos.price
            }, { merge: true })
            .then(doc => {
                console.log(doc)
            })
        e.preventDefault();
    }

    const handleDelete = (e) => {
        const id = props.match.params.id;
        console.log("Eliminar", id)
        firebase.db.doc("productos/" + id)
            .delete()
            .then(doc => {
                console.log(doc)
            })
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nombre</label>
                    <input type="text" name="name"  class="form-control" aria-describedby="emailHelp" value={datos.name} onChange={handleChange}></input>
                </div>
                <div>
                    <label>Precio</label>
                    <input type="number" name="price" value={datos.price} onChange={handleChange}></input>
                </div>
                <input type="submit" value="Guardar"></input>
                <button onClick={handleDelete}>Eliminar</button>
            </form>
        </div>
    )
}

export default ProductoEditar
