import React, { Component } from "react";
import './Producto.css';

class Producto extends Component {
  constructor() {
    super();

    this.state = {      
      mensaje:""}
  }

  comprar = () => {
    this.setState({mensaje:"Gracias por su compra"});
  }

  render() {
    const productoDetalle = this.props;

    return (

      <div>
        <h1 className="titulo"> Trabajo Práctico Optativo N°1 </h1>
        <h2>Detalle de producto</h2>

        <div>Nombre: {productoDetalle.productoNombre}</div>
        <div>Descripción: {productoDetalle.productoDescripcion}</div>
        <div>Precio: {productoDetalle.productoPrecio}</div>
        <div>SKU: {productoDetalle.productoSKU}</div>
        <div>Cantidad: {productoDetalle.productoCantidad}</div>

        <button className="boton" onClick={this.comprar}>Comprar</button>
        <div>{this.state.mensaje}</div>


      </div>

    )
  }
}

export default Producto;
