import React, { Component } from "react";
import './Producto.css';

class Producto extends Component {
  constructor() {
    super();

    this.state = {
      mensaje: ""
    }
  }

  comprar = () => {
    this.setState({ mensaje: "¡Gracias por su compra!" });
  }

  render() {
    const productoDetalle = this.props.productoDetalle;

    return (

      <div>
        <h1 className="titulo_1"> Finalizar compra </h1>
        <h2 className="titulo_2">Detalle de producto</h2>

        <div>
          <table>
            <tr>
              <td><h4 className="titulo_detalle">Nombre:</h4></td>
              <td> {productoDetalle.productoNombre}</td>
            </tr>

            <tr>
              <td><h4 className="titulo_detalle">Descripción:</h4></td>
              <td> <div>{productoDetalle.productoDescripcion}</div></td>
            </tr>

            <tr>
              <td><h4 className="titulo_detalle">Precio:</h4></td>
              <td> <div>{productoDetalle.productoPrecio}</div></td>
            </tr>

            <tr>
              <td><h4 className="titulo_detalle">SKU:</h4></td>
              <td> <div>{productoDetalle.productoSKU}</div></td>
            </tr>

            <tr>
              <td><h4 className="titulo_detalle">Cantidad:</h4></td>
              <td> <div>{productoDetalle.productoCantidad}</div></td>
            </tr>

          </table>
        </div>

        <div>
          <button className="boton" onClick={this.comprar}>Comprar</button>
          <h3> className="mensaje_compra">{this.state.mensaje} </h3>
        </div>
      </div>
    )
  }
}

export default Producto;
